		second := x[1]

		if a.ID != first.AdditivesID {
			t.Error("foreign key was wrong value", a.ID, first.AdditivesID)
		}
		if a.ID != second.AdditivesID {
			t.Error("foreign key was wrong value", a.ID, second.AdditivesID)
		}

